  initGt,
  codeSend,
  loginRegister,
  accountLogin,
  leigodLogin,
  checkPhoneIsBind,
  autoAccountLogin,
  bindPhoneLogin,
  findPasswordLogin,
  userInfoLogin,
  autoUserInfoLogin,
  advertiseLogin
} from '../api/login'

export default mainWindow => {
  // 级验证
  ipcMain.on('init-gt', (event, params) => {
    initGt()
      .then(res => {
        mainWindow.send('init-gt-res', res)
      })
      .catch(err => {
        mainWindow.send('init-gt-res', err)
      })
  })

  // 获取验证码
  ipcMain.on('code-send', (event, params, cb) => {
    // params.func
    codeSend(params)
      .then(res => {
        mainWindow.send('code-send-res', res, cb)
      })
      .catch(err => {
        console.log('error', err)
        mainWindow.send('code-send-res', err, cb)
      })
  })

