            commit(UPDATE_NEWTABLISTDATA, res)
            commit(UPDATE_TOTAL, res.total)
            } catch (e) {
            }
            commit(UPDATE_NEWLOADING, false)
        },
    }

    export default {
        namespaced: true,
        state,
        getters,
        mutations,
        actions
    }
