import { mapGetters, mapActions } from 'vuex'
export default {
  name: 'preview',
  data () {
    return {
      preview: {},
      advice: '',
      centerDialogVisible: false,
      approveOrDeny: ''
    }
  },
  props: {
    detail: {
      type: Object,
      default: () => {
        return {
          fileNames: '',
          title: '',
          source: '',
          cids: '',
          lifestyle: '',
          segment: '',
          focus: '',
          brandFocus: '',
          targetDemographicCare: '',
          year: '',
          language: '',
          region: '',
          country: '',
          keywords: '',
          imgFileId: '',
          imgFileSrc: ''
        }
      }
    },
    tradeBeans: {
      type: Array,
      default: () => []
    },
    conceptFiles: {
      type: Array,
      default: () => []
    },
    videoFiles: {
      type: Array,
      default: () => []
    },
    performanceFiles: {
      type: Array,
      default: () => []
    },
    formulations: {
      type: Array,
      default: () => []
    },
    isShow: {
      type: Boolean,
      default: false
    },
    trendFiles: {
      type: Array,
      default: () => []
    },
    previewOrApprove: {
      type: String,
      default: 'upload-denied'
    }
  },
  methods: {
    ...mapActions("concept", [
      "getApprovalListApi",
      "getTableDataApi"
    ]),

    // 点击通过与驳回
    approve (el) {
      this.approveOrDeny = el
      this.centerDialogVisible = true
      // this.getManagementPreviewDataApi();
    },
    async confirmSubmit () {
      let id = this.detail.id
      switch (this.approveOrDeny) {
        case 'Approve': {
          let status = 4
          await this.getApprovalListApi({id, status})
          break
        }
        case 'Deny': {
          let status = 2
          let advice = this.advice
          await this.getApprovalListApi({id, status, advice})
          break
        }
      }
      let paramsData = {
        size: 10,
        current: 1,
        desc: ["id"]
      }
      /** 渲染数据管理里的审核中列表 */
      await this.getTableDataApi({ ...paramsData, tableSource: 0, status: 1 })
      // 刷新审核中的总条数
      await this.getAppendingTotalDataApi(this.pager.total)
      this.$emit('close-dialog')
      this.centerDialogVisible = false
    }
  },
  computed: {
    ...mapGetters("concept", [
      // "managementPreviewList",
      "approvalList",
      "pager"
    ]),
    isVisible: {
      get () {
        return this.isShow
      },
      set (val) {
        this.$emit('close-dialog')
      }
    }
  }
}



